import React, { useEffect, useState}  from "react";
import { MDBCol} from 'mdbreact';
import "./Thread.css";

function Thread({name, message, url}) {
    const [roommie, setRoommate] = useState([])

    useEffect(() => {
        API.getRoommies()
        .then(data => 
            {
                console.log(data)
                setRoommate(data.data)
            }
        )

      }, [])

    function handleChange(event) {
        const {key, style} = event.target;

        setRoommate(roommie => {
            return{
                ...roommie,
                [key]: style
            }
        })
    }
    return (
        <div>
        {roommie.map((roommie => (

        <div className="thread">
            <MDBCol mb="1" md="1" className="mb-3 text-center">
            <img onChange= {handleChange} src={roommie.url} className="rounded-circle" alt={roommie.name} data-holder-rendered="true"/>
          </MDBCol>
          <div className= "convo_needs">
              <h3 className= "name">{roommie.name}</h3>
              <p className= "message">{message}</p>
          </div>

        </div>

    )))
}
</div>
    )
}

export default Thread;
